import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';
import SpotifyWebApi from 'spotify-web-api-js';
import { makeStyles } from '@material-ui/core/styles';
const spotifyApi = new SpotifyWebApi();

function App() {
    const CLIENT_ID = "7bedd495cf6a43b49f1655cbbc2be4c4"
    const REDIRECT_URI = "http://localhost:3000"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const SCOPE = "user-read-private user-top-read"
    const classes = useStyles();
    const useStyles = makeStyles({
        app: {
          textAlign: 'center',
          backgroundColor: '#282c34',
          minHeight: '100vh',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          color: 'white',
          padding: '20px'
        },
        header: {
          fontSize: '2.5rem',
          marginBottom: '30px',
        },
        score: {
          fontSize: '2rem',
          marginBottom: '20px',
        },
        trackContainer: {
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-around',
          width: '100%',
          marginBottom: '50px',
        },
        trackCard: {
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          width: '30%',
          backgroundColor: '#61DAFB',
          color: '#282c34',
          padding: '20px',
          borderRadius: '10px',
          boxShadow: '0px 5px 15px rgba(0, 0, 0, 0.2)',
        },
        trackImg: {
          width: '100%',
          marginBottom: '15px',
          borderRadius: '10px',
        },
        trackName: {
          fontSize: '1.2rem',
          marginBottom: '10px',
        },
        trackPopularity: {
          fontSize: '1rem',
        },
        vsLabel: {
          fontSize: '2rem',
          color: '#61DAFB',
        },
        button: {
          margin: '10px',
          backgroundColor: '#282c34',
          color: '#61DAFB',
          border: 'none',
          borderRadius: '10px',
          padding: '10px 20px',
          cursor: 'pointer',
          fontSize: '1rem',
        },
      });

    const [token, setToken] = useState("")

    const [searchKey, setSearchKey] = useState("")
    const [artists, setArtists] = useState([])
    const [topSongs, setTopSongs] = useState([])
    const [track1, setTrack1] = useState()
    const [track2, setTrack2] = useState()
    const [score, setScore] = useState(0)
    const [seen, setSeen] = useState(new Set())

    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        setToken(token)
        document.getElementById("score").style.display = "none"
        if (token) {
            document.getElementById("start").style.display = ""
        } else {
            document.getElementById("start").style.display = "none"
        }

    }, [])

    const logout = () => {
        setToken("")
        setTrack1()
        setTrack2()
        document.getElementById("score").style.display = "none"
        document.getElementById("start").style.display = "none"
        setScore(0)
        window.localStorage.removeItem("token")
    }

    const getTopSongsDiffTimes = async (e) => {
        const {data} = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
            headers: {
                Authorization: `Bearer ${token}`
            },
            params: {
                time_range: e,
                limit: 50
            }
        })
        setTopSongs(topSongs => [...topSongs, ...(data.items)])
    }

    const getTopSongs = async () => {
        document.getElementById("score").style.display = ""
        getTopSongsDiffTimes("short_term")
        getTopSongsDiffTimes("medium_term")
        getTopSongsDiffTimes("long_term")
        newTrack(3)

    }

    const renderArtists = () => {
        artists.map(artist => (
            <div key={artist.id}>
                {artist.images.length ? <img width={"50%"} src={artist.images[0].url} alt=""/> : <div>No Image</div>}
                {artist.name}
            </div>
        ))
    }

    const gameSelection = (event, choice, other) => {
        if (choice.popularity >= other.popularity) {
            setScore(score + 1)
            setTrack1(track2)
            newTrack(2)
        } else {
            setScore(0)
            seen.clear()
            newTrack(3)
        }
    }

    const newTrack = (num) => {
        if (num == 1) {
            this.setTrack1(topSongs[parseInt(Math.random() * (topSongs.length - 1))])
            /*
            while (seen.has(track1)) {
                newTrack(1)
            }*/
            seen.add(track1)
        } else if (num == 2){
            setTrack2(topSongs[parseInt(Math.random() * (topSongs.length - 1))])
            /*
            while (seen.has(track2)) {
                newTrack(2)
            }*/
            seen.add(track2)
        } else {
            setTrack1(topSongs[parseInt(Math.random() * (topSongs.length - 1))])
            /*
            while (seen.has(track1)) {
                newTrack(1)
            } */
            setTrack2(topSongs[parseInt(Math.random() * (topSongs.length - 1))])
            /*
            while (seen.has(track2)) {
                newTrack(2)
            }*/
            seen.add(track1)
            seen.add(track2)
        }
        console.log(seen.size)
    }


    const renderTopSongs = () => {
        topSongs.map(track => (
            <div key={track.id}>
                {track.album.images.length ? <img width={"20%"} src={track.album.images[0].url} alt=""/> : <div>No Image</div>}
                {track.name}
            </div>
        ))



        if (track1 && track2 && track1.id===track2.id) {
            console.log(track1.name + "    " + track2.name)
            newTrack(2) 
        }

    }

    return (

        <div className={classes.app}>
      <header className={classes.header}>More or Less?</header>
      <h2 className={classes.score}>Score: {score}</h2>
      <div className={classes.trackContainer}>
      <div className={classes.trackCard}>
        <img className={classes.trackImg} src={track1.album.images[0].url} onClick={(event) => gameSelection(event, track1, track2)} alt=""/>
        <p className={classes.trackName}>{track1.name}</p>
        <p className={classes.trackPopularity}>{track1.popularity}</p>
      </div>
      <p className={classes.vsLabel}>VS</p>
      <div className={classes.trackCard}>
        <p className={classes.trackName}>{track2.name}</p>
        <img className={classes.trackImg} src={track2.album.images[0].url} alt=""/>
        <div className={classes.btnContainer}>
          <button className={classes.button} onClick={(event) => gameSelection(event, track2, track1)}>More Popular</button>
          <button className={classes.button} onClick={(event) => gameSelection(event, track1, track2)}>Less Popular</button>
        </div>
        <p className={classes.trackPopularity}>{track2.popularity}</p>
      </div>
    </div>
  </div>


        /*
        <div className="App">
            <header className="App-header">
                <h1>More or Less?</h1>
                <h2 id="score" >Score: {score}</h2>
                {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login to Spotify</a>
                    : <button className="logout" onClick={logout}>Logout</button>}
                <div>
                    <button id="start" onClick={getTopSongs}>Start</button>
                </div>
                {renderTopSongs()}
                {track1 && track2 ? 
                <div className="container">
                    <div className="track1">
                        <img id="track1-img" width={"30%"} src={track1.album.images[0].url} onClick={(event) => gameSelection(event, track1, track2)} alt=""/>
                        <p>{track1.name}</p>
                        {track1 ? <p>{track1.popularity}</p> : <div></div>}
                    </div>
                    <p className="vsLabel"> VS. </p>
                    <div className="track2">
                        <p>IS</p>
                        <img id="track2-img" width={"30%"} src={track2.album.images[0].url} alt=""/>
                        <p>{track2.name}</p>
                        <button onClick={(event) => gameSelection(event, track2, track1)}>More Popular</button>
                        <button onClick={(event) => gameSelection(event, track1, track2)}>Less Popular</button>
                        {track2 ? <p>{track2.popularity}</p> : <div></div>}
                    </div>
                </div> : <div></div>}
                {renderArtists()}

            </header>
        </div>
        */
    );

};

export default App;
