import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';
import SpotifyWebApi from 'spotify-web-api-js';
const spotifyApi = new SpotifyWebApi();

function App() {
    const CLIENT_ID = "7bedd495cf6a43b49f1655cbbc2be4c4"
    const REDIRECT_URI = "http://localhost:3000"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const SCOPE = "user-read-private user-top-read"

    const [token, setToken] = useState("")
    const [topTracks, setTopTracks] = useState([])

    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]

            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        setToken(token)

    }, [])

    const logout = () => {
        setToken("")
        window.localStorage.removeItem("token")
    }

    const userTracks = async (e) => {
        e.preventDefault()
        const {data} = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
            headers: {
                Authorization: `Bearer ${token}`
            },
            params: {
                time_range: "short_term",
                limit: 50
            }
        })

        setTopTracks(data.items)
    }

    const renderTracks = () => {
        return topTracks.map(track => (
            <div key={track.id}>
                {track.images.length ? <img width={"100%"} src={track.images[0].url} alt=""/> : <div>No Image</div>}
                {track.name}
            </div>
        ))
    }




    return (
                <div className="App">
                {userTracks}
                  <header className="App-header">
                    <h1>Higher or Lower: Songs Edition</h1>
                    <div className="score-card">
                        <div className="score">Score: 0</div>
                    </div>
                    <div className="game-container">
                      <div className="song-card">
                        <img src="song1.jpg" alt="Song 1 Image"/>
                        <h2>Song 1</h2>
                      </div>
                      <div className="song-card">
                        <img src="song2.jpg" alt="Song 2 Image"/>
                        <h2>Song 2</h2>
                        <button className="higher-button">Higher</button>
                        <button className="lower-button">Lower</button>
                      </div>
                    </div>
                    {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login
                        to Spotify</a>
                    : <button onClick={logout}>Logout</button>}
                  </header>
                </div>
    );

};

export default App;
