// Note: Add the code that you have previously written for the game inside this component
import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GamePage = ({ onLogout, token }) => {
    const CLIENT_ID = "7bedd495cf6a43b49f1655cbbc2be4c4"
    const REDIRECT_URI = "http://localhost:3000"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const SCOPE = "user-read-private user-top-read"

    const [topTracks, setTopTracks] = useState([])
    const [song1, setSong1] = useState(null);
    const [song2, setSong2] = useState(null);
    const [choiceMade, setChoiceMade] = useState(false);
    const [choice, setChoice] = useState(null);

    const pickRandomSongs = () => {
        const randomSongs = topTracks.sort(() => 0.5 - Math.random()).slice(0,1);
        setSong1(song2);
        setSong2(randomSongs[0]);
    }

    useEffect(() => {
        userTracks();
    }, [token]);  // updated useEffect hook

    const logout = () => {
        onLogout();
    }

    const userTracks = async () => {
        if (token) {
            try {
                const timeRanges = ["short_term", "medium_term", "long_term"];
                let allTracks = [];

                for (let timeRange of timeRanges) {
                    const { data } = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
                        headers: {
                            Authorization: `Bearer ${token}`
                        },
                        params: {
                            time_range: timeRange,
                            limit: 50
                        }
                    });

                    allTracks = [...allTracks, ...data.items];
                }

                setTopTracks(allTracks);
                const randomSongs = topTracks.sort(() => 0.5 - Math.random()).slice(0,2);
                setSong1(randomSongs[0]);
                setSong2(randomSongs[1]);

            } catch (error) {
                console.error("Failed to fetch user's top tracks: ", error);
            }
        }
    };

    const handleButtonClick = (choice) => {
        setChoice(choice);
        setChoiceMade(true);
        setTimeout(() => {
            pickRandomSongs();
            setChoiceMade(false);
            setChoice(null);
        }, 2000);
    };


    return (
        <div className="App">
            <header className="App-header">
                <h1>Higher or Lower: Songs Edition</h1>
                <div className="score-card">
                    <div className="score">Score: 0</div>
                </div>
                <div className="game-container">
                <div className="song-card">
                    {song1 ? (
                        <>
                            <img src={song1.album.images[0].url} alt={song1.name} />
                            <h2>{song1.name}</h2>
                            <p>{song1.artists.map(artist => artist.name).join(', ')}</p>
                            <p>Popularity: {song1.popularity}</p>
                        </>
                    ) : (
                        <p>Loading...</p>
                    )}
                </div>
                <div className="song-card">
                    {song2 ? (
                        <>
                            <img src={song2.album.images[0].url} alt={song2.name} />
                            <h2>{song2.name}</h2>
                            <p>{song2.artists.map(artist => artist.name).join(', ')}</p>
                            {choiceMade && <p>Popularity: {song2.popularity}</p>}
                            {!choiceMade ? (
                                <>
                                    <button className="higher-button" onClick={() => handleButtonClick('higher')}>Higher</button>
                                    <button className="lower-button" onClick={() => handleButtonClick('lower')}>Lower</button>
                                </>
                            ) : null}
                        </>
                    ) : (
                        <p>Loading...</p>
                    )}
                </div>
                </div>
                {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login
                        to Spotify</a>
                    : <button onClick={logout}>Logout</button>}
            </header>
        </div>
    );
};

export default GamePage;
