import './App.css';
import {useEffect, useState} from 'react';
import axios from 'axios';
import SpotifyWebApi from 'spotify-web-api-js';

const spotifyApi = new SpotifyWebApi();

function App() {
    const CLIENT_ID = "7bedd495cf6a43b49f1655cbbc2be4c4"
    const REDIRECT_URI = "http://localhost:3000"
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const SCOPE = "user-read-private user-top-read"

    const [token, setToken] = useState("")
    const [topTracks, setTopTracks] = useState([])
    const [song1, setSong1] = useState(null);
    const [song2, setSong2] = useState(null);

    useEffect(() => {
        const hash = window.location.hash
        let token = window.localStorage.getItem("token")

        if (!token && hash) {
            token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1]
            window.location.hash = ""
            window.localStorage.setItem("token", token)
        }

        if (token) {
            setToken(token)
            userTracks()
        }
    }, [])

    useEffect(() => {
        if (token) {
            userTracks();
        }
    }, [token]);

    const logout = () => {
        setToken("")
        setTopTracks([])
        window.localStorage.removeItem("token")
    }

    const userTracks = async () => {
        if (token) {
            try {
                const { data } = await axios.get("https://api.spotify.com/v1/me/top/tracks", {
                    headers: {
                        Authorization: `Bearer ${token}`
                    },
                    params: {
                        time_range: "short_term",
                        limit: 50
                    }
                });

                setTopTracks(data.items);

                const randomSongs = data.items.sort(() => 0.5 - Math.random()).slice(0,2);
                setSong1(randomSongs[0]);
                setSong2(randomSongs[1]);
            } catch (error) {
                console.error("Failed to fetch user's top tracks: ", error);
            }
        }
    };

    const renderTracks = () => {
        return topTracks.map(track => (
            <div key={track.id}>
                {track.album.images.length ? <img width={"100%"} src={track.album.images[0].url} alt=""/> : <div>No Image</div>}
                {track.name}
            </div>
        ))
    }

    const handleButtonClick = () => {
        setSong1(song2);
        const randomSong = topTracks[Math.floor(Math.random() * topTracks.length)];
        setSong2(randomSong);
    }


    return (
        <div className="App">
            <header className="App-header">
                <h1>Higher or Lower: Songs Edition</h1>
                <div className="score-card">
                    <div className="score">Score: 0</div>
                </div>
                <div className="game-container">
                    <div className="song-card">
                        {song1 ? (
                            <>
                            <img src={song1.album.images[0].url} alt={song1.name} />
                            <h2>{song1.name}</h2>
                            <p>{song1.artists.map(artist => artist.name).join(', ')}</p>
                            </>
                        ) : (
                            <p>Loading...</p>
                        )}
                    </div>
                    <div className="song-card">
                        {song2 ? (
                            <>
                            <img src={song2.album.images[0].url} alt={song2.name} />
                            <h2>{song2.name}</h2>
                            <p>{song2.artists.map(artist => artist.name).join(', ')}</p>
                            <button className="higher-button" onClick={handleButtonClick}>Higher</button>
                            <button className="lower-button" onClick={handleButtonClick}>Lower</button>
                            </>
                        ) : (
                            <p>Loading...</p>
                        )}
                    </div>
                </div>
                {!token ?
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login
                        to Spotify</a>
                    : <button onClick={logout}>Logout</button>}
            </header>
        </div>
    );
};

export default App;
